import React, { useState } from 'react';
import './App.css';

function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

function App() {
  const [numColors, setNumColors] = useState(5);
  const [colors, setColors] = useState(Array.from({ length: 5 }, getRandomColor));

  const handleNumColorsChange = (e) => {
    const value = Math.max(1, Math.min(20, Number(e.target.value)));
    setNumColors(value);
    setColors(Array.from({ length: value }, (_, i) => colors[i] || getRandomColor()));
  };

  const generatePalette = () => {
    setColors(Array.from({ length: numColors }, getRandomColor));
  };

  const handleColorChange = (index, newColor) => {
    const newColors = [...colors];
    newColors[index] = newColor;
    setColors(newColors);
  };

  const copyToClipboard = (color) => {
    navigator.clipboard.writeText(color);
  };

  return (
    <div className="App">
      <h1>Generador de Paleta de Colores</h1>
      <div style={{ marginBottom: 16 }}>
        <label>
          Cantidad de colores:
          <input
            type="number"
            min="1"
            max="20"
            value={numColors}
            onChange={handleNumColorsChange}
            style={{ width: 50, marginLeft: 8 }}
          />
        </label>
        <button onClick={generatePalette} style={{ marginLeft: 16 }}>
          Generar paleta aleatoria
        </button>
      </div>
      <div className="palette">
        {colors.map((color, idx) => (
          <div key={idx} className="color-block" style={{ background: color }}>
            <input
              type="color"
              value={color}
              onChange={e => handleColorChange(idx, e.target.value)}
              className="color-picker"
            />
            <span className="color-code">{color}</span>
            <button className="copy-btn" onClick={() => copyToClipboard(color)}>
              Copiar
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
